mapper.select:
SELECT
(SELECT s.SITE_NAME FROM KF_SITE s WHERE 1=1 and s.SITE_CODE = t.SITE_CODE) AS SITE_NAME ,

FROM KF_DEVICE t
WHERE 1=1
AND t.DELETE_FLAG = #{deleteFlag}
AND t.DEVICE_CODE = #{deviceCode}

SELECT
(SELECT s.SITE_NAME FROM KF_SITE s WHERE 1=1 and s.SITE_CODE = t.SITE_CODE) AS SITE_NAME ,

FROM KF_DEVICE t
WHERE 1=1
AND t.DELETE_FLAG = #{deleteFlag}
AND t.DEVICE_SERIAL_NUMBER = #{deviceSerialNumber}

SELECT
    COUNT(*)
FROM KF_DEVICE t
WHERE 1=1
AND t.DELETE_FLAG = #{deleteFlag}

    AND t.DEVICE_CODE = #{deviceCode}


    AND t.DEVICE_SERIAL_NUMBER = #{deviceSerialNumber}


    AND t.SITE_CODE = #{siteCode}


    AND t.DEVICE_NAME LIKE CONCAT(CONCAT('%', #{deviceName}), '%')


    AND t.DEVICE_STATUS = #{deviceStatus}

SELECT
(SELECT s.SITE_NAME FROM KF_SITE s WHERE 1=1 and s.SITE_CODE = t.SITE_CODE) AS SITE_NAME ,

FROM KF_DEVICE t
WHERE 1=1
AND t.DELETE_FLAG = #{deleteFlag}

    AND t.DEVICE_CODE = #{deviceCode}


    AND t.DEVICE_SERIAL_NUMBER = #{deviceSerialNumber}


    AND t.SITE_CODE = #{siteCode}


    AND t.DEVICE_NAME LIKE  CONCAT(CONCAT('%', #{deviceName}), '%')


    AND t.DEVICE_STATUS = #{deviceStatus}

ORDER BY t.MODIFY_TIME DESC,t.CREATE_TIME DESC

    LIMIT #{startNum},#{pageSize}

mapper.update:
UPDATE
KF_DEVICE
SET
DEVICE_STATUS = #{deviceStatus},
MODIFY_TIME = #{modifyTime}
WHERE 1=1
AND DEVICE_CODE = #{deviceCode}
AND DELETE_FLAG = #{deleteFlag}
